#include <algorithm>#include <cstdio>#include <cstring>using namespace std;int main() {    char str[100];    int count[30] = {0,}, max_cnt = 0;    while (scanf("%s", str) != EOF) {        for(size_t i = 0; i < strlen(str); i++) {            if (str[i] >= 'A' && str[i] <= 'Z') {                ++count[str[i] - 'A'];                max_cnt = max(max_cnt, count[str[i] - 'A']);            }        }    }    for (int i = max_cnt; i >= 1; i--) {        int blank = 0;        if (count[0] >= i)            printf("*");        else            ++blank;        for (int j = 1; j < 26; j++) {            if (count[j] >= i) {                while (blank--)                    printf(" ");                printf(" *");                blank = 0;            } else                blank += 2;        }        printf("\n");    }    printf("A");    for (char c = 'B'; c <= 'Z'; c++)        printf(" %c", c);    return 0;}